package employee.management.system.client;// Importing necessary libraries for GUI, networking, and event handlingimport javax.swing.*;import java.awt.*;import java.awt.event.*;import java.io.*;import java.net.*;public class Main_class extends JFrame {    // Socket and IO streams for communicating with the server    private Socket clientSocket;    private PrintWriter out;    private BufferedReader in;    // Constructor to build the main UI    public Main_class() {        // Get the socket and streams from ConnectionManager        this.clientSocket = ConnectionManager.getClientSocket();   // Gets the client's socket connection        this.out = ConnectionManager.getOutputStream();            // Gets the output stream to send data to server        this.in = ConnectionManager.getInputStream();              // Gets the input stream to receive data from server        // Load and set the background image        ImageIcon i1 = new ImageIcon(ClassLoader.getSystemResource("icons/home.jpg"));  // Load the image from resources        Image i2 = i1.getImage().getScaledInstance(1120,630,Image.SCALE_DEFAULT);       // Resize the image        ImageIcon i3 = new ImageIcon(i2);                                               // Convert resized image back to ImageIcon        JLabel img = new JLabel(i3);                                                    // Create JLabel to hold image        img.setBounds(0,0,1120,630);                                                    // Set position and size        add(img);                                                                       // Add image to JFrame        // Heading label        JLabel heading = new JLabel("Employee Management System");                      // Create title label        heading.setBounds(340,155,400,40);                                              // Position the label        heading.setFont(new Font("Raleway", Font.BOLD, 25));                            // Set font style        img.add(heading);                                                               // Add heading on top of image        // "Add Employee" Button        JButton add = new JButton("Add Employee");                                      // Create button        add.setBounds(335,270,150,40);                                                  // Set position        add.setForeground(Color.WHITE);                                                 // Set text color        add.setBackground(Color.black);                                                 // Set button background        add.addActionListener(new ActionListener() {                                    // Add click listener            @Override            public void actionPerformed(ActionEvent e) {                // Run the AddEmployee window in a new thread                new Thread(() -> {                    new AddEmployee();                                                 // Open AddEmployee window                    setVisible(false);                                                 // Hide current window                }).start();            }        });        img.add(add);                                                                   // Add button to the image label        // "View Employee" Button        JButton view = new JButton("View Employee");                                    // Create button        view.setBounds(565,270,150,40);                                                 // Set position        view.setForeground(Color.WHITE);                                                // Set text color        view.setBackground(Color.black);                                                // Set button background        view.addActionListener(new ActionListener() {                                   // Add click listener            @Override            public void actionPerformed(ActionEvent e) {                // Run the View_Employee window in a new thread                new Thread(() -> {                    new View_Employee();                                               // Open View_Employee window                    setVisible(false);                                                 // Hide current window                }).start();            }        });        img.add(view);                                                                  // Add button to image label        // "Remove Employee" Button        JButton rem = new JButton("Remove Employee");                                   // Create button        rem.setBounds(440,370,150,40);                                                  // Set position        rem.setForeground(Color.WHITE);                                                 // Set text color        rem.setBackground(Color.black);                                                 // Set button background        rem.addActionListener(new ActionListener() {                                    // Add click listener            @Override            public void actionPerformed(ActionEvent e) {                // Run the RemoveEmployee window in a new thread                new Thread(() -> {                    new RemoveEmployee();                                              // Open RemoveEmployee window                }).start();            }        });        img.add(rem);                                                                   // Add button to image label        // Frame settings        setSize(1120,630);                                                              // Set window size        setLocation(250,100);                                                           // Set window position        setLayout(null);                                                                // Disable default layout manager        setVisible(true);                                                               // Make the window visible    }    public static void main(String[] args) {        // Entry point of the application        // This is where the Main_class frame would typically be launched    }}